import { HttpClient } from '@angular/common/http';
import { Component, Inject } from '@angular/core';
import { Router } from '@angular/router';
import { NewGroup } from '../models/new-group';

@Component({
  selector: 'app-group-create',
  templateUrl: './group-create.component.html',
  styleUrls: ['./group-create.component.css']
})
export class GroupCreateComponent {
  group: NewGroup = new NewGroup();
  file?: File;

  loading = false;

  http: HttpClient;
  baseUrl: string;
  router: Router;

  constructor(http: HttpClient, @Inject('BASE_URL') baseUrl: string, router: Router) {
    this.http = http;
    this.baseUrl = baseUrl;
    this.router = router;
  }

  onSubmit() {
    this.loading = true;
    this.http.post(this.baseUrl + '/api/groups', this.group)
      .subscribe(
        result => {
          this.router.navigate(['/', 'groups']);
        },
        error => {
          
        }
      );
  }

  onFileSelected(event: any) {
    this.file = event.target.files[0];
  }
}
<div class="d-flex justify-content-center">
  <div class="card w-25">
    <h4 class="card-header">New Group</h4>
    <div class="card-body">
      <form (ngSubmit)="onSubmit()">
        <div class="mb-3">
          <label for="name" class="form-label">Name</label>
          <input [(ngModel)]="group.name"
                 id="name" name="name"
                 type="text" class="form-control"
                 required #name="ngModel" />
          <div *ngIf="name.invalid && (name.dirty || name.touched)"
               class="alert alert-danger mt-3">

            <div *ngIf="name.errors?.['required']">
              Name is required.
            </div>

          </div>
        </div>

        <div class="mb-3">
          <label for="description" class="form-label">Description</label>
          <textarea class="form-control" id="description" rows="3"></textarea>
        </div>

        <div class="mb-3">
          <label for="cover" class="form-label">Cover</label>
          <input class="form-control" type="file" id="cover" accept="image/*" (change)="onFileSelected($event)">
        </div>

        <div>
          <button [disabled]="loading" class="btn btn-primary">
            <span *ngIf="loading" class="spinner-border spinner-border-sm me-1"></span>
            Create
          </button>
        </div>
      </form>
    </div>
  </div>
</div>
