import { HttpClient } from '@angular/common/http';
import { Component, Inject, Input } from '@angular/core';
import { AuthenticationRequest } from '../models/auth/authentication-request';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  authRequest: AuthenticationRequest = new AuthenticationRequest();
  loading = false;

  http: HttpClient;
  baseUrl: string;

  constructor(http: HttpClient, @Inject('BASE_URL') baseUrl: string) {
    this.http = http;
    this.baseUrl = baseUrl;
  }

  ngOnInit() {
  }

  onSubmit() {
    this.loading = true;
    this.http.post(this.baseUrl + 'api/auth/bearertoken', this.authRequest)
      .subscribe(
        response => {
          this.loading = true;
        },
        error => {
          this.loading = true;
        }
      );
  }
}
<div class="d-flex justify-content-center">
  <div class="card w-25">
    <h4 class="card-header">Login</h4>
    <div class="card-body">
      <form (ngSubmit)="onSubmit()">
        <div class="mb-3">
          <label for="username" class="form-label">Username</label>
          <input [(ngModel)]="authRequest.username"
                 id="username" name="username"
                 type="text" class="form-control"
                 required #username="ngModel" />
          <div *ngIf="username.invalid && (username.dirty || username.touched)"
               class="alert">

            <div *ngIf="username.errors?.['required']">
              Name is required.
            </div>

          </div>
        </div>
        <div class="mb-3">
          <label for="password" class="form-label">Password</label>
          <input [(ngModel)]="authRequest.password" id="password" name="password" type="password" class="form-control" ng-model="password" />
        </div>
        <div>
          <button [disabled]="loading" class="btn btn-primary">
            <span *ngIf="loading" class="spinner-border spinner-border-sm me-1"></span>
            Login
          </button>
          <a routerLink="../register" class="btn btn-link">Register</a>
        </div>
      </form>
    </div>
  </div>
</div>
