import { HttpClient } from '@angular/common/http';
import { Component, Inject } from '@angular/core';
import { NewUser } from '../models/auth/new-user';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {
  user: NewUser = new NewUser();

  loading = false;

  http: HttpClient;
  baseUrl: string;

  constructor(http: HttpClient, @Inject('BASE_URL') baseUrl: string) {
    this.http = http;
    this.baseUrl = baseUrl;
  }

  ngOnInit() {
  }

  onSubmit() {
    this.loading = true;
    this.http.post(this.baseUrl + 'api/auth/bearertoken', this.user)
      .subscribe(
        response => {
          this.loading = true;
        },
        error => {
          this.loading = true;
        }
      );
  }
}
<div class="d-flex justify-content-center">
  <div class="card w-25">
    <h4 class="card-header">Login</h4>
    <div class="card-body">
      <form (ngSubmit)="onSubmit()">
        <div class="mb-3">
          <label for="email" class="form-label">Email</label>
          <input [(ngModel)]="user.email"
                 id="email" name="email"
                 type="text" class="form-control"
                 required email="true"
                 #email="ngModel" />
          <div *ngIf="email.invalid && (email.dirty || email.touched)"
               class="alert alert-danger mt-3">

            <div *ngIf="email.errors?.['required']">
              Email is required.
            </div>

            <div *ngIf="email.errors?.['email']">
              Not a valid email.
            </div>

          </div>
        </div>

        <div class="mb-3">
          <label for="username" class="form-label">Username</label>
          <input [(ngModel)]="user.username"
                 id="username" name="username"
                 type="text" class="form-control"
                 required #username="ngModel" />
          <div *ngIf="username.invalid && (username.dirty || username.touched)"
               class="alert alert-danger mt-3">

            <div *ngIf="username.errors?.['required']">
              Username is required.
            </div>

          </div>
        </div>
        <div class="mb-3">
          <label for="password" class="form-label">Password</label>
          <input [(ngModel)]="authRequest.password" id="password" name="password" type="password" class="form-control" ng-model="password" />
        </div>
        <div>
          <button [disabled]="loading" class="btn btn-primary">
            <span *ngIf="loading" class="spinner-border spinner-border-sm me-1"></span>
            Register
          </button>
          <a routerLink="../login" class="btn btn-link">Login</a>
        </div>
      </form>
    </div>
  </div>
</div>
